package Lec_2;

public class Odd_Even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//flowchart
		//start
		//read a number
		//check condition with the help of if else
		//
		
		//if hai toh else ana mandatory nahi hai
		//else ke liye if hona hi chahiye
		
		int n = 87;
		if(n > 67) {
			System.out.println("Hello");
		}
		if(n % 2 == 0) { //87 % 2...1 ayega and 1 is not equal to 0 hence odd.
			System.out.println("Even");
		}
		else {
			System.out.println("Odd");
		}
	}

}
